// frontend/src/components/AdminDashboard.js
import React, { useState, useEffect } from "react";

// --- Main Admin Dashboard Component ---
const AdminDashboard = ({ api }) => {
  const [activeTab, setActiveTab] = useState("flights"); // 'flights', 'users', 'employees', 'report'

  return (
    <div className="bg-white p-8 rounded-lg shadow-xl min-h-[600px]">
      <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>
      <div className="border-b border-gray-200">
        <nav className="-mb-px flex space-x-8" aria-label="Tabs">
          <TabButton
            activeTab={activeTab}
            tabName="flights"
            onClick={setActiveTab}
          >
            Manage Flights & Costs
          </TabButton>
          <TabButton
            activeTab={activeTab}
            tabName="users"
            onClick={setActiveTab}
          >
            Manage User Tokens
          </TabButton>
          <TabButton
            activeTab={activeTab}
            tabName="employees"
            onClick={setActiveTab}
          >
            Employee Management
          </TabButton>
          <TabButton
            activeTab={activeTab}
            tabName="report"
            onClick={setActiveTab}
          >
            Financial Report
          </TabButton>
        </nav>
      </div>

      <div className="mt-8">
        {activeTab === "flights" && <FlightManager api={api} />}
        {activeTab === "users" && <UserManager api={api} />}
        {activeTab === "employees" && <EmployeeManager api={api} />}
        {activeTab === "report" && <FinancialReport api={api} />}
      </div>
    </div>
  );
};

// --- Helper Components for Tabs and Modals ---
const TabButton = ({ activeTab, tabName, onClick, children }) => (
  <button
    onClick={() => onClick(tabName)}
    className={`${
      activeTab === tabName
        ? "border-blue-500 text-blue-600"
        : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
    } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm transition-colors`}
  >
    {children}
  </button>
);

const Modal = ({ children, onClose }) => (
  <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex justify-center items-center">
    <div className="bg-white p-8 rounded-lg shadow-2xl w-full max-w-md relative">
      <button
        onClick={onClose}
        className="absolute top-4 right-4 text-gray-500 hover:text-gray-800 text-2xl"
      >
        &times;
      </button>
      {children}
    </div>
  </div>
);

